package com.saifintex.domain;

public class UsersRelation extends BaseDomain {

	public UsersRelation(){
		// TODO Auto-generated constructor stub
	}

	private int id;
	private int user1;
	private int user2;
	private Relations relation;
	private Relations oppositeRelation;
	
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public int getUser1() {
		return user1;
	}
	public void setUser1(int user1) {
		this.user1 = user1;
	}
	public int getUser2() {
		return user2;
	}
	public void setUser2(int user2) {
		this.user2 = user2;
	}
	public Relations getRelation() {
		return relation;
	}
	public void setRelation(Relations relation) {
		this.relation = relation;
	}
	public Relations getOppositeRelation() {
		return oppositeRelation;
	}
	public void setOppositeRelation(Relations oppositeRelation) {
		this.oppositeRelation = oppositeRelation;
	}	
	
	
}
